<?phpclass ControllerCommonFooter extends Controller {	public function index() {		$this->load->language('common/footer');		$data['scripts'] = $this->document->getScripts('footer');		$data['text_information'] = $this->language->get('text_information');		$data['text_service'] = $this->language->get('text_service');		$data['text_extra'] = $this->language->get('text_extra');		$data['text_contact'] = $this->language->get('text_contact');		$data['text_return'] = $this->language->get('text_return');		$data['text_sitemap'] = $this->language->get('text_sitemap');		$data['text_manufacturer'] = $this->language->get('text_manufacturer');		$data['text_voucher'] = $this->language->get('text_voucher');		$data['text_affiliate'] = $this->language->get('text_affiliate');		$data['text_special'] = $this->language->get('text_special');		$data['text_account'] = $this->language->get('text_account');		$data['text_order'] = $this->language->get('text_order');		$data['text_wishlist'] = $this->language->get('text_wishlist');		$data['text_newsletter'] = $this->language->get('text_newsletter');		$this->document->addStyle('catalog/view/javascript/btt/backtotop.css');		$this->document->addScript('catalog/view/javascript/btt/modernizr.js');		$this->document->addScript('catalog/view/javascript/btt/main.js');		/*$this->load->model('information/information');		$data['informations'] = array();		foreach ($this->model_information_information->getInformations() as $result) {			if ($result['bottom']) {				$data['informations'][] = array(					'title' => $result['title'],					'href'  => $this->url->link('information/information', 'information_id=' . $result['information_id'])				);			}		}*/		$data['contact'] = $this->url->link('information/contact');		$data['return'] = $this->url->link('account/return/add', '', true);		$data['sitemap'] = $this->url->link('information/sitemap');		$data['manufacturer'] = $this->url->link('product/manufacturer');		$data['voucher'] = $this->url->link('account/voucher', '', true);		$data['affiliate'] = $this->url->link('affiliate/account', '', true);		$data['special'] = $this->url->link('product/special');		$data['account'] = $this->url->link('account/account', '', true);		$data['order'] = $this->url->link('account/order', '', true);		$data['wishlist'] = $this->url->link('account/wishlist', '', true);		$data['newsletter'] = $this->url->link('account/newsletter', '', true);		$config_name = $this->config->get('config_name');		$data['config_name'] = $config_name[$this->config->get('config_language_id')];		$config_address = $this->config->get('config_address');		$data['config_address'] = $config_address[$this->config->get('config_language_id')];		$data['config_telephone'] = $this->config->get('config_telephone');		$data['config_email'] = $this->config->get('config_email');		$data['powered'] = sprintf($this->language->get('text_powered'), $data['config_name'], date('Y', time()));		// Social		$data['config_social_facebook'] = $this->config->get('config_social_facebook');		$this->load->model('menu/menu');		$data['menus'] = array();		$menu_group_id = $this->config->get('manager_footer_menu');		$results = $this->model_menu_menu->getMenus(0, $menu_group_id);		$data['search'] = $this->load->controller('common/search');		foreach ($results as $result) {			$get_name = $this->model_menu_menu->getNameMenu($result['menu_id'], $result['menu_type_id'], $result['name_id']);			$data['menus'][] = array(				'menu_id'   => $result['menu_id'],				'name'      => $get_name['name'],				'href'      => $get_name['href'],				'attribute' => $get_name['attribute']			);		}		return $this->load->view('common/footer', $data);	}	public function getShareProduct(){		$json = '<div class="addthis_default_style"><a class="addthis_button_compact">Chia sáº»</a> <a class="addthis_button_email"></a><a class="addthis_button_print"></a> <a class="addthis_button_facebook"></a> <a class="addthis_button_twitter"></a></div><script type="text/javascript" src="//s7.addthis.com/js/250/addthis_widget.js"></script>';		$this->response->addHeader('Content-Type: application/json');		$this->response->setOutput(json_encode($json));	}}