<?phpclass ControllerModuleResponsiveslide extends Controller {	public function index($setting) {		static $module = 0;		$this->load->model('design/banner');		$this->load->model('tool/image');		$this->document->addStyle('catalog/view/javascript/responsiveslide/responsiveslides.css');		$this->document->addScript('catalog/view/javascript/responsiveslide/responsiveslides.min.js');		$data['banners'] = array();		$results = $this->model_design_banner->getBanner($setting['banner_id']);		foreach ($results as $result) {			if (is_file(DIR_IMAGE . $result['image'])) {				$data['banners'][] = array(					'title' => $result['title'],					'link'  => $result['link'],					'image' => $this->model_tool_image->resize($result['image'], $setting['width'], $setting['height'], 'resize_and_crop')				);			}		}		$data['module'] = $module++;		return $this->load->view('module/responsiveslide', $data);	}}