<?phpclass ControllerInstallLicence extends Controller{    private $error = array();    public function index()    {        $this->language->load('install/licence');        if (isset($this->request->get['config_success'])) {            $data['config_success'] = 1;        } else {            $data['config_success'] = 0;        }        if (($this->request->server['REQUEST_METHOD'] == 'POST')) {            $licence = $this->request->post['licence'];            $file = fopen(DIR_HTHUNG . 'config/license.txt', 'w');            fwrite($file, $licence);            fclose($file);            $this->response->redirect($this->url->link('install/licence&config_success=1'));        }        $this->document->setTitle($this->language->get('heading_title'));        $data['heading_title'] = $this->language->get('heading_title');        $data['text_licence'] = $this->language->get('text_licence');        $data['entry_licence'] = $this->language->get('entry_licence');        $data['button_config'] = $this->language->get('button_config');        $data['action'] = $this->url->link('install/licence');        if (isset($this->request->post['licence'])) {            $data['licence'] = $this->request->post['licence'];        } else {            $data['licence'] = '';            $file = fopen(DIR_HTHUNG . 'config/license.txt', 'r') or exit("error");            while (!feof($file)) {                $data['licence'] .= fgets($file);                echo fgets($file);            }            fclose($file);        }        $data['footer'] = $this->load->controller('common/footer');        $data['header'] = $this->load->controller('common/header');        $this->response->setOutput($this->load->view('install/licence', $data));    }}